<?phpnamespace app\admin\model\system;use basic\ModelBasic;use think\Db;use traits\ModelTrait;class SystemRemarks extends ModelBasic {    use ModelTrait;    public static function getTableInfoRemarks(){          //  表注释          Db::query("alter table eb_store_coupon_issue comment '优惠券前台领取表'");          Db::query("alter table eb_store_coupon_issue_user comment '优惠券前台用户领取记录表'");          Db::query("alter table eb_store_pink comment '拼团表'");          Db::query("alter table eb_user_notice_see comment '用户通知表'");          Db::query("alter table eb_user_extract comment '用户提现表'");          Db::query("alter table eb_user_group comment '用户分组表'");          Db::query("alter table eb_user_recharge comment '用户充值表'");           //  字段注释          //  缓存表  字段          Db::query("alter table eb_cache modify column key varchar(32) comment '缓存key唯一值'");          Db::query("alter table eb_cache modify column result text comment '缓存数据'");          Db::query("alter table eb_cache modify column add_time int(10) comment '缓存时间'");          //优惠券前台领取表 字段        Db::query("alter table eb_store_coupon_issue modify column cid int(10) comment '优惠券ID'");        Db::query("alter table eb_store_coupon_issue modify column start_time int(10) comment '优惠券领取开启时间'");        Db::query("alter table eb_store_coupon_issue modify column end_time int(10) comment '优惠券领取结束时间'");        Db::query("alter table eb_store_coupon_issue modify column total_count int(10) comment '优惠券领取数量'");        Db::query("alter table eb_store_coupon_issue modify column remain_count int(10) comment '优惠券剩余领取数量'");        Db::query("alter table eb_store_coupon_issue modify column add_time int(10) comment '优惠券添加时间'");        //  优惠券前台用户领取记录表 字段        Db::query("alter table eb_store_coupon_issue_user modify column uid int(10) comment '领取优惠券用户ID'");        Db::query("alter table eb_store_coupon_issue_user modify column issue_coupon_id int(10) comment '优惠券前台领取ID'");        Db::query("alter table eb_store_coupon_issue_user modify column add_time int(10) comment '领取时间'");        //用户分组表  字段        Db::query("alter table eb_user_group modify column group_name varchar(64) comment '用户分组名称'");        //用户充值表 字段        Db::query("alter table eb_user_recharge modify column uid int(10) comment '充值用户UID'");        Db::query("alter table eb_user_recharge modify column order_id varchar(32) comment '订单号'");        Db::query("alter table eb_user_recharge modify column price decimal(8,2) comment '充值金额'");        Db::query("alter table eb_user_recharge modify column recharge_type varchar(32) comment '充值类型'");        Db::query("alter table eb_user_recharge modify column paid tinyint(1) comment '是否充值'");        Db::query("alter table eb_user_recharge modify column pay_time int(10) comment '充值支付时间'");        Db::query("alter table eb_user_recharge modify column add_time int(12) comment '充值时间'");    }}